mapper.select:
SELECT
-- 级联表的字段 ,shicixuexi.shicixuexi_uuid_number as shicixuexiUuidNumber ,shicixuexi.shicixuexi_name as shicixuexiName ,shicixuexi.shicixuexi_photo as shicixuexiPhoto ,shicixuexi.shicixuexi_video as shicixuexiVideo ,shicixuexi.shicixuexi_file as shicixuexiFile ,shicixuexi.shicixuexi_types as shicixuexiTypes ,shicixuexi.shicixuexi_clicknum as shicixuexiClicknum ,shicixuexi.shicixuexi_content as shicixuexiContent ,shicixuexi.shangxia_types as shangxiaTypes ,shicixuexi.shicixuexi_delete as shicixuexiDelete ,yonghu.yonghu_name as yonghuName ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_id_number as yonghuIdNumber ,yonghu.yonghu_photo as yonghuPhoto ,yonghu.yonghu_email as yonghuEmail
FROM shicixuexi_collection a left JOIN shicixuexi shicixuexi ON a.shicixuexi_id = shicixuexi.id left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.id in
#{item}
and ( a.shicixuexi_id = #{params.shicixuexiId} )
and ( a.yonghu_id = #{params.yonghuId} )
and a.shicixuexi_collection_types = #{params.shicixuexiCollectionTypes}
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and a.shicixuexi_id IS NOT NULL
and shicixuexi.shicixuexi_uuid_number like CONCAT('%',#{params.shicixuexiUuidNumber},'%')
and shicixuexi.shicixuexi_name like CONCAT('%',#{params.shicixuexiName},'%')
and shicixuexi.shicixuexi_video like CONCAT('%',#{params.shicixuexiVideo},'%')
and shicixuexi.shicixuexi_types = #{params.shicixuexiTypes}
and shicixuexi.shicixuexi_clicknum >= #{params.shicixuexiClicknumStart}
and shicixuexi.shicixuexi_clicknum <= #{params.shicixuexiClicknumEnd}
and shicixuexi.shicixuexi_clicknum = #{params.shicixuexiClicknum}
and shicixuexi.shicixuexi_content like CONCAT('%',#{params.shicixuexiContent},'%')
and shicixuexi.shangxia_types = #{params.shangxiaTypes}
and shicixuexi.shicixuexi_delete >= #{params.shicixuexiDeleteStart}
and shicixuexi.shicixuexi_delete <= #{params.shicixuexiDeleteEnd}
and shicixuexi.shicixuexi_delete = #{params.shicixuexiDelete}
and a.yonghu_id IS NOT NULL
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')
order by a.${params.orderBy} desc
